CREATE TABLE Categoria (id INT AUTO_INCREMENT NOT NULL, nombre VARCHAR(255) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Ciudad (id INT AUTO_INCREMENT NOT NULL, nombre VARCHAR(100) NOT NULL, slug VARCHAR(100) NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Mensaje (id INT AUTO_INCREMENT NOT NULL, respuesta_id INT DEFAULT NULL, fecha DATE NOT NULL, oferta_id INT NOT NULL, texto VARCHAR(255) NOT NULL, UNIQUE INDEX UNIQ_54DE249DD9BA57A2 (respuesta_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE ofertas (fecha DATE NOT NULL, usuario_id INT NOT NULL, motivo VARCHAR(255) DEFAULT NULL, monto DOUBLE PRECISION NOT NULL, vendida TINYINT(1) NOT NULL, INDEX IDX_48C925F3DB38439E (usuario_id), PRIMARY KEY(fecha, usuario_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Producto (id INT AUTO_INCREMENT NOT NULL, categoria_id INT DEFAULT NULL, f_ini DATE NOT NULL, f_fin DATE NOT NULL, nombre VARCHAR(255) NOT NULL, descripcion VARCHAR(255) NOT NULL, foto VARCHAR(255) NOT NULL, INDEX IDX_5ECD64433397707A (categoria_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE Usuario (id INT AUTO_INCREMENT NOT NULL, ciudad_id INT DEFAULT NULL, nombre VARCHAR(100) NOT NULL, apellido VARCHAR(100) NOT NULL, mail VARCHAR(100) NOT NULL, dni INT NOT NULL, tarjeta INT NOT NULL, direccion VARCHAR(255) NOT NULL, f_nac DATE NOT NULL, f_alta DATE NOT NULL, pass VARCHAR(255) NOT NULL, tipo VARCHAR(255) NOT NULL, estado TINYINT(1) NOT NULL, INDEX IDX_EDD889C1E8608214 (ciudad_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
CREATE TABLE usuario_producto (usuario_id INT NOT NULL, producto_id INT NOT NULL, INDEX IDX_4F7AF48CDB38439E (usuario_id), INDEX IDX_4F7AF48C7645698E (producto_id), PRIMARY KEY(usuario_id, producto_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci ENGINE = InnoDB;
